import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [greeting, setGreeting] = useState('');

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/greeting')
      .then(response => {
        setGreeting(response.data.greeting);
      })
      .catch(error => {
        console.error("There was an error fetching the greeting!", error);
      });
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>{greeting}</h1>

        <div class="content">
        <div class="buttton-image">
            <img src="/test.jpeg" alt="My Image" />
        </div>
        <div class="text-overlay">
            <span>ダイナミックな挨拶ウェブページへようこそ！</span>
            <span>Welcome to my dynamic greeting webpage!</span>
            <button onClick={() => alert('Hello! Have a nice day!')}>Click me</button>
        </div>
    </div>

      </header>
    </div>
  );
}

export default App;
